import React, { createContext, useState, useContext, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) throw new Error("useAuth must be used within AuthProvider");
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(localStorage.getItem("token"));

  // Setup axios defaults whenever token changes
  useEffect(() => {
    if (token) {
      axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      fetchCurrentUser();
    } else {
      delete axios.defaults.headers.common["Authorization"];
      setUser(null);
      setLoading(false);
    }
  }, [token]);

  // Fetch the currently logged-in user
  const fetchCurrentUser = async () => {
    try {
      const response = await axios.get("/api/auth/me");
      setUser(response.data.user || response.data);
    } catch (error) {
      console.warn(
        "âš ï¸ Error fetching user:",
        error.response?.data || error.message
      );
      if (error.response?.status === 401) {
        toast.warn("Session expired. Please log in again.");
        logout();
      }
    } finally {
      setLoading(false);
    }
  };

  // Login
  const login = async (email, password) => {
    if (!email || !password) {
      toast.error("Email and password are required");
      return;
    }

    try {
      const response = await axios.post("/api/auth/login", { email, password });
      const { token: newToken, user: userData } = response.data;

      localStorage.setItem("token", newToken);
      setToken(newToken);
      setUser(userData);
      axios.defaults.headers.common["Authorization"] = `Bearer ${newToken}`;

      toast.success("âœ… Login successful!");
      return userData;
    } catch (error) {
      console.error("Login error:", error.response || error.message);
      const message =
        error.response?.data?.message ||
        "Login failed. Please check credentials or try again later.";
      toast.error(message);
      throw error;
    }
  };

  // Register
  const register = async (userData) => {
    try {
      const response = await axios.post("/api/auth/register", userData);
      const { token: newToken, user: newUser } = response.data;

      localStorage.setItem("token", newToken);
      setToken(newToken);
      setUser(newUser);
      axios.defaults.headers.common["Authorization"] = `Bearer ${newToken}`;

      toast.success("ðŸŽ‰ Registration successful!");
      return newUser;
    } catch (error) {
      console.error("Registration error:", error.response || error.message);
      const message =
        error.response?.data?.message ||
        "Registration failed. Please try again later.";
      toast.error(message);
      throw error;
    }
  };

  // Logout
  const logout = () => {
    localStorage.removeItem("token");
    delete axios.defaults.headers.common["Authorization"];
    setToken(null);
    setUser(null);
    toast.info("ðŸ‘‹ Logged out successfully");
  };

  // Update profile
  const updateProfile = async (profileData) => {
    try {
      const response = await axios.put("/api/auth/profile", profileData);
      setUser(response.data.user);
      toast.success("âœ… Profile updated successfully!");
      return response.data.user;
    } catch (error) {
      console.error("Profile update error:", error.response || error.message);
      const message =
        error.response?.data?.message || "Profile update failed";
      toast.error(message);
      throw error;
    }
  };

  const value = {
    user,
    loading,
    login,
    register,
    logout,
    updateProfile,
    isAuthenticated: !!user,
  };

  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
